package utilities;

import org.springframework.security.crypto.codec.Base64;

public class Utilities {

	//Est metodo transforma una cadena de byte a un string en base64 para que pueda ser leido como imagen en HTML
	public static String showImage(final byte[] picture, String defaultImage) {

		String result;
		byte[] base64;
		StringBuilder imageString;
		imageString = new StringBuilder();
		imageString.append("data:image/png;base64,");

		if (picture != null) {
			base64 = Base64.encode(picture);
			imageString.append(new String(base64));
			result = imageString.toString();
		} else {
			if(defaultImage.equals("noImage"))
				imageString.append("");
			
			if(defaultImage.equals("vehicle"))
				imageString.append("");
			
			if(defaultImage.equals("user"))
				imageString.append("");
				
			if(defaultImage.equals("interrogation"))
				imageString.append("");
			
		}

		result = imageString.toString();
		return result;
	}
	
	
	public static int[] getPages(int page, int nElem ,int collectionSize){
		
		int pageBegin = (page-1) * nElem;
		int pageEnd = (page*nElem) -1;
		int pageNumbers = collectionSize/nElem;
		if(collectionSize%nElem > 0){
			pageNumbers++;
		}
		return new int[]{pageBegin,pageEnd,pageNumbers};
	}
}
